// export default Header;
import { Link } from 'react-router-dom';

const Header = () => {
  const handleLogout = () => {
    // Remove token from localStorage and redirect to login page
    localStorage.removeItem('token');
    window.location.href = '/login';  // Redirect to login
  };

  // Check if the user is authenticated
  const isLoggedIn = !!localStorage.getItem('token');

  return (
    <header className="bg-gray-800 text-white py-4">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <div className="text-2xl font-bold">
          <Link to="/" className="text-white hover:text-gray-300">
            My App
          </Link>
        </div>
        {isLoggedIn && (
          <nav className="space-x-4">
            <Link to="/search" className="text-white hover:text-gray-300">
              Search
            </Link>
            <Link to="/lists" className="text-white hover:text-gray-300">
              Saved Lists
            </Link>
          </nav>
        )}
        {isLoggedIn && (
          <button
            onClick={handleLogout}
            className="text-white bg-red-600 hover:bg-red-700 px-4 py-2 rounded"
          >
            Logout
          </button>
        )}
      </div>
    </header>
  );
};

export default Header;
