return (
  <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500">
    <div className="w-full max-w-md p-8 space-y-6 bg-white border border-gray-200 rounded-lg shadow-xl">
      <h2 className="text-3xl font-bold text-center text-gray-800">Welcome Back</h2>
      <p className="text-center text-gray-500">Login to your account</p>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-sm font-medium text-gray-700">Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            required
            className="w-full p-3 mt-1 border border-gray-300 rounded-lg focus:ring-purple-500 focus:border-purple-500"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
            className="w-full p-3 mt-1 border border-gray-300 rounded-lg focus:ring-purple-500 focus:border-purple-500"
          />
        </div>
        {error && <p className="text-sm text-red-600" aria-live="assertive">{error}</p>}
        <button
          type="submit"
          disabled={loading}
          className={`w-full py-3 text-lg font-semibold text-white rounded-lg focus:ring-4 focus:ring-opacity-50 ${
            loading
              ? 'bg-gray-400 cursor-not-allowed'
              : 'bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600'
          }`}
        >
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
      <button
        onClick={handleSignup}
        className="w-full py-3 mt-4 text-lg font-semibold text-purple-600 border border-purple-500 rounded-lg hover:bg-purple-500 hover:text-white focus:ring-4 focus:ring-purple-500 focus:ring-opacity-50"
      >
        Sign Up
      </button>
    </div>
  </div>
